import React, { useState } from 'react';
import './App.css';
import Quiz from './Quiz';
import Header from './Header';
import StartScreen from './StartScreen';
import { FullPageChat } from "flowise-embed-react";

function App() {
  const [showQuiz, setShowQuiz] = useState(false);
  const [started, setStarted] = useState(false);

  const newsArticles = [
    {
      title: "마약 중독 예방을 위한 새로운 교육 프로그램 출시",
      summary: "정부는 청소년을 대상으로 한 새로운 마약 중독 예방 교육 프로그램을 발표했습니다.",
      link: "#"
    },
    {
      title: "마약 중독자 재활 성공률 증가",
      summary: "최근 연구에 따르면, 새로운 재활 프로그램의 도입으로 마약 중독자의 재활 성공률이 크게 증가했습니다.",
      link: "#"
    },
    {
      title: "마약 근절 캠페인, 시민들의 호응 얻어",
      summary: "지역 사회에서 시작된 마약 근절 캠페인이 시민들의 적극적인 참여로 큰 성과를 거두고 있습니다.",
      link: "#"
    },
    {
      title: "마약 중 치료를 위한 새로운 약물 개발",
      summary: "과학자들이 마약 중독 치료에 효과적인 새로운 약물을 개발했다고 발표했습니다.",
      link: "#"
    },
    {
      title: "청소년 마약 사용 증가 추세, 전문가들 우려",
      summary: "최근 통계에 따르면 청소년들의 마약 사용이 증가하고 있어 전문가들이 우려를 표명하고 있습니다.",
      link: "#"
    },
    {
      title: "마약 밀매 조직 검거, 대규모 마약 유통망 와해",
      summary: "경찰은 대규모 마약 밀매 조직을 검거하여 국내 마약 유통망에 큰 타격을 주었다고 밝혔습니다.",
      link: "#"
    },
    {
      title: "마약 중독 예방을 위한 가상현실(VR) 프로그램 도입",
      summary: "학교에서 학생들의 마약 중독 예방 교육에 가상현실 기술을 활용한 새로운 프로그램이 도입되었습니다.",
      link: "#"
    },
    {
      title: "마약 중독자 가족 지원 센터 개소",
      summary: "마약 중독자의 가족들을 위한 전문 상담 및 지원 센터가 새롭게 문을 열었습니다.",
      link: "#"
    },
    {
      title: "국제 협력으로 대규모 마약 밀수 적발",
      summary: "여러 국가의 협력으로 대규모 국제 마약 밀수 조직이 적발되어 큰 양의 마약이 압수되었습니다.",
      link: "#"
    },
    {
      title: "마약 중독 회복 수기 공모전 개최",
      summary: "마약 중독에서 회복한 사람들의 경험담을 공유하는 수기 공모전이 개최되어 많은 관심을 받고 있습니다.",
      link: "#"
    }
  ];

  const handleStart = () => {
    setStarted(true);
  };

  const handleHomeClick = () => {
    setShowQuiz(false);
  };

  if (!started) {
    return <StartScreen onStart={handleStart} />;
  }

  return (
    <div className="App">
      <Header 
        showQuizButton={!showQuiz} 
        onQuizClick={() => setShowQuiz(true)} 
        onHomeClick={handleHomeClick}
      />
      <main className="App-main">
        {showQuiz ? (
          <Quiz />
        ) : (
          <>
            <section className="chatbot-section">
              <div className="chat-container">
                <FullPageChat
                  chatflowid="bf97e04d-7f6e-4500-b490-e4ee644f9526"
                  apiHost="https://flow.edutrack.kr"
                  theme={{
                    chatWindow: {
                      width: '100%',
                      height: '100%',
                      containerWidth: '100%',
                      containerHeight: '100%',
                    }
                  }}
                />
              </div>
            </section>
            <section className="news-section">
              <h2>관련 뉴스</h2>
              <div className="article-container">
                <div className="article-scroll">
                  {newsArticles.concat(newsArticles).map((article, index) => (
                    <article key={index} className="news-article">
                      <h3>{article.title}</h3>
                      <p>{article.summary}</p>
                      <a href={article.link} target="_blank" rel="noopener noreferrer">자세히 보기</a>
                    </article>
                  ))}
                </div>
              </div>
            </section>
          </>
        )}
      </main>
      <footer className="App-footer">
        <p>© 2023 마약 예방 교육 센터. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
