import React, { useState } from 'react';
import './Quiz.css';

const quizData = [
  {
    question: "메트암페타민과 암페타민의 차이점은 무엇인가요?",
    options: [
      "메트암페타민은 비의료적 사용이 많고, 암페타민은 의료용으로 주로 사용된다.",
      "메트암페타민은 낮은 중독성을 가지며, 암페타민은 높은 중독성을 가진다.",
      "메트암페타민은 항상 합법이며, 암페타민은 불법이다.",
      "메트암페타민과 암페타민은 동일한 화학 구조를 가진다."
    ],
    correctAnswer: 0
  },
  {
    question: "마약류 관리법에 따라 마약류의 소지 및 유통을 금지하는 이유는 무엇인가요?",
    options: [
      "마약류가 일반 의약품과 유사하게 사용될 수 있기 때문에",
      "마약류가 중독성과 건강에 해로운 영향을 미칠 수 있기 때문에",
      "마약류가 모든 연령대에서 안전하게 사용될 수 있기 때문에",
      "마약류가 사회적으로 긍정적인 영향을 미칠 수 있기 때문에"
    ],
    correctAnswer: 1
  },
  {
    question: "마약류의 정의에 포함되지 않는 것은 무엇인가요?",
    options: [
      "대마와 그 추출물",
      "마약 성분이 포함된 진통제",
      "향정신성의약품과 그 제조물",
      "일반 의약품에 사용되는 비타민"
    ],
    correctAnswer: 3
  },
  {
    question: "의료용 마약류의 올바른 사용에 대한 올바른 기준은 무엇인가요?",
    options: [
      "개인의 필요에 따라 자유롭게 사용하기",
      "처방받지 않은 경우에도 사용하는 것 허용",
      "반드시 필요한 경우에만 사용하기",
      "마약류를 안전하게 보관하는 것이 우선"
    ],
    correctAnswer: 2
  },
  {
    question: "마약 예방 교육을 위한 효과적인 가족 자조 모임의 주요 목적은 무엇인가요?",
    options: [
      "법적 처벌의 강화",
      "약물 사용에 대한 정보 제공",
      "사회적 낙인을 증가시키기",
      "약물 남용의 위험성 무시하기"
    ],
    correctAnswer: 1
  }
];

function Quiz() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);

  const handleAnswerClick = (selectedAnswerIndex) => {
    if (selectedAnswerIndex === quizData[currentQuestion].correctAnswer) {
      setScore(score + 1);
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < quizData.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  const restartQuiz = () => {
    setCurrentQuestion(0);
    setScore(0);
    setShowScore(false);
  };

  return (
    <div className="quiz-container">
      {showScore ? (
        <div className="score-section">
          당신의 점수: {score} / {quizData.length}
          <button onClick={restartQuiz}>   다시 시작</button>
        </div>
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>질문 {currentQuestion + 1}</span>/{quizData.length}
            </div>
            <div className="question-text">{quizData[currentQuestion].question}</div>
          </div>
          <div className="answer-section">
            {quizData[currentQuestion].options.map((option, index) => (
              <button key={index} onClick={() => handleAnswerClick(index)}>
                {option}
              </button>
            ))}
          </div>
        </>
      )}
    </div>
  );
}

export default Quiz;
