import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
  const [chatInput, setChatInput] = useState('');
  const [chatMessages, setChatMessages] = useState([]);
  const articleContainerRef = useRef(null);

  // 예시 뉴스 데이터 (10개로 확장)
  const newsArticles = [
    {
      title: "마약 중독 예방을 위한 새로운 교육 프로그램 출시",
      summary: "정부는 청소년을 대상으로 한 새로운 마약 중독 예방 교육 프로그램을 발표했습니다.",
      link: "#"
    },
    {
      title: "마약 중독자 재활 성공률 증가",
      summary: "최근 연구에 따르면, 새로운 재활 프로그램의 도입으로 마약 중독자의 재활 성공률이 크게 증가했습니다.",
      link: "#"
    },
    {
      title: "마약 근절을 위한 국제 협력 강화",
      summary: "전 세계 국가들이 마약 근절을 위한 협력을 강화하기로 합의했습니다.",
      link: "#"
    },
    {
      title: "신종 마약의 위험성에 대한 경고",
      summary: "보건당국은 최근 유행하는 신종 마약의 심각한 부작용에 대해 경고했습니다.",
      link: "#"
    },
    {
      title: "마약 중독 치료를 위한 혁신적인 의료기술 개발",
      summary: "과학자들이 마약 중독 치료에 획기적인 도움을 줄 수 있는 새로운 의료기술을 개발했습니다.",
      link: "#"
    },
    {
      title: "청소년 마약 사용 증가 추세에 대한 우려",
      summary: "최근 통계에 따르면 청소년들의 마약 사용이 증가하고 있어 사회적 우려가 커지고 있습니다.",
      link: "#"
    },
    {
      title: "마약 중독자 가족 지원 프로그램 확대",
      summary: "정부는 마약 중독자 가족들을 위한 심리 상담 및 경제적 지원 프로그램을 확대하기로 결정했습니다.",
      link: "#"
    },
    {
      title: "마약 퇴치 캠페인의 효과 분석 결과 발표",
      summary: "장기간 진행된 마약 퇴치 캠페인의 효과에 대한 분석 결과가 발표되었습니다.",
      link: "#"
    },
    {
      title: "마약 중독 예방을 위한 직장 내 프로그램 의무화 추진",
      summary: "정부는 직장 내 마약 중독 예방 프로그램 의무화를 위한 법안을 준비 중입니다.",
      link: "#"
    },
    {
      title: "마약 중독으로부터의 회복 체험담 공유 행사 개최",
      summary: "마약 중독에서 회복한 사람들의 경험을 공유하는 대규모 행사가 열립니다.",
      link: "#"
    }
  ];

  const handleChatSubmit = (e) => {
    e.preventDefault();
    if (chatInput.trim() !== '') {
      setChatMessages([...chatMessages, { type: 'user', content: chatInput }]);
      // 여기에 챗봇 응답 로직을 추가할 수 있습니다.
      setChatInput('');
    }
  };

  useEffect(() => {
    const scrollInterval = setInterval(() => {
      if (articleContainerRef.current) {
        const container = articleContainerRef.current;
        const scrollAmount = container.clientWidth / 3; // 한 기사의 너비만큼 스크롤

        if (container.scrollLeft + container.clientWidth >= container.scrollWidth) {
          // 끝에 도달하면 처음으로 돌아감
          container.scrollTo({ left: 0, behavior: 'smooth' });
        } else {
          container.scrollBy({ left: scrollAmount, behavior: 'smooth' });
        }
      }
    }, 5000); // 5초마다 스크롤

    return () => clearInterval(scrollInterval);
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>마약 예방 교육 센터</h1>
      </header>
      <main className="App-main">
        <section className="chatbot-section">
          <h2>챗봇에게 물어보세요</h2>
          <div className="chat-messages">
            {chatMessages.map((message, index) => (
              <div key={index} className={`chat-message ${message.type}`}>
                {message.content}
              </div>
            ))}
          </div>
          <form onSubmit={handleChatSubmit} className="chat-input-form">
            <input
              type="text"
              value={chatInput}
              onChange={(e) => setChatInput(e.target.value)}
              placeholder="질문을 입력하세요..."
              className="chat-input"
            />
            <button type="submit" className="chat-submit">전송</button>
          </form>
        </section>
        <section className="news-section">
          <h2>관련 뉴스</h2>
          <div className="article-container" ref={articleContainerRef}>
            {newsArticles.map((article, index) => (
              <article key={index} className="news-article">
                <h3>{article.title}</h3>
                <p>{article.summary}</p>
                <a href={article.link} target="_blank" rel="noopener noreferrer">자세히 보기</a>
              </article>
            ))}
          </div>
        </section>
      </main>
      <footer className="App-footer">
        <p>© 2023 마약 예방 교육 센터. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
