import React, { useState } from 'react';
import './App.css';

function App() {
  const [chatInput, setChatInput] = useState('');
  const [chatMessages, setChatMessages] = useState([]);

  const handleChatSubmit = (e) => {
    e.preventDefault();
    if (chatInput.trim() !== '') {
      setChatMessages([...chatMessages, { type: 'user', content: chatInput }]);
      // 여기에 챗봇 응답 로직을 추가할 수 있습니다.
      setChatInput('');
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>마약 예방 교육 센터</h1>
      </header>
      <main className="App-main">
        <section className="info-section">
          <h2>마약의 위험성</h2>
          <p>마약은 심각한 건강 문제와 사회적 문제를 일으킬 수 있습니다. 여기에서 마약의 위험성에 대해 자세히 알아보세요.</p>
        </section>
        <section className="education-section">
          <h2>예방 교육 프로그램</h2>
          <ul>
            <li>청소년 대상 교육</li>
            <li>성인 대상 워크샵</li>
            <li>온라인 교육 과정</li>
          </ul>
        </section>
        <section className="resources-section">
          <h2>도움 받을 수 있는 곳</h2>
          <p>마약 중독으로 어려움을 겪고 있다면, 아래 연락처로 도움을 요청하세요:</p>
          <p>긴급전화: 1234-5678</p>
        </section>
        <section className="chatbot-section">
          <h2>챗봇에게 물어보세요</h2>
          <div className="chat-messages">
            {chatMessages.map((message, index) => (
              <div key={index} className={`chat-message ${message.type}`}>
                {message.content}
              </div>
            ))}
          </div>
          <form onSubmit={handleChatSubmit} className="chat-input-form">
            <input
              type="text"
              value={chatInput}
              onChange={(e) => setChatInput(e.target.value)}
              placeholder="질문을 입력하세요..."
              className="chat-input"
            />
            <button type="submit" className="chat-submit">전송</button>
          </form>
        </section>
      </main>
      <footer className="App-footer">
        <p>© 2023 마약 예방 교육 센터. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
