import React, { useState, useEffect } from 'react';
import './App.css';
import Quiz from './Quiz';
import sum_news_gpt from '../functions/news_sum';
import Header from './Header';
import StartScreen from './StartScreen';
import { FullPageChat } from "flowise-embed-react";

function App() {
  const [showQuiz, setShowQuiz] = useState(false);
  const [started, setStarted] = useState(false);
  const [newsArticles, setNewsArticles] = useState([]);

  useEffect(() => {
    // 뉴스 기사를 가져오는 API 호출
    fetch('/api/news')
      .then(response => response.json())
      .then(data => {
        // 각 기사에 대해 요약 생성
        const summarizePromises = data.map(article =>
          fetch('/api/summarize', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ content: article.content, title: article.title }),
          }).then(res => res.json())
        );

        // 모든 요약이 완료되면 상태 업데이트
        Promise.all(summarizePromises).then(summaries => {
          const articles = data.map((article, index) => ({
            title: article.title,
            summary: summaries[index].summary,
            link: article.url
          }));
          setNewsArticles(articles);
        });
      })
      .catch(error => console.error('뉴스를 가져오는 중 오류 발생:', error));
  }, []);

  const handleStart = () => {
    setStarted(true);
  };

  const handleHomeClick = () => {
    setShowQuiz(false);
  };

  if (!started) {
    return <StartScreen onStart={handleStart} />;
  }

  return (
    <div className="App">
      <Header 
        showQuizButton={!showQuiz} 
        onQuizClick={() => setShowQuiz(true)} 
        onHomeClick={handleHomeClick}
      />
      <main className="App-main">
        {showQuiz ? (
          <Quiz />
        ) : (
          <>
            <section className="chatbot-section">
              <div className="chat-container">
                <FullPageChat
                  chatflowid="bf97e04d-7f6e-4500-b490-e4ee644f9526"
                  apiHost="https://flow.edutrack.kr"
                  theme={{
                    chatWindow: {
                      width: '100%',
                      height: '100%',
                      containerWidth: '100%',
                      containerHeight: '100%',
                    }
                  }}
                />
              </div>
            </section>
            <section className="news-section">
              <h2>관련 뉴스</h2>
              <div className="article-container">
                <div className="article-scroll">
                  {newsArticles.map((article, index) => (
                    <article key={index} className="news-article">
                      <h3>{article.title}</h3>
                      <p>{article.summary}</p>
                      <a href={article.link} target="_blank" rel="noopener noreferrer">자세히 보기</a>
                    </article>
                  ))}
                </div>
              </div>
            </section>
          </>
        )}
      </main>
      <footer className="App-footer">
        <p>© 2023 마약 예방 교육 센터. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
