import React, { useState } from 'react';
import './Quiz.css';

const quizData = [
  {
    question: "마약의 주요 위험성 중 하나는?",
    options: ["체중 증가", "피부 미백", "중독성", "수면 과다"],
    correctAnswer: "중독성"
  },
  {
    question: "다음 중 합법적인 약물은?",
    options: ["헤로인", "코카인", "처방된 진통제", "메스암페타민"],
    correctAnswer: "처방된 진통제"
  },
  {
    question: "마약 중독 치료의 첫 단계는?",
    options: ["운동", "중독 인정", "여행", "새 직업 찾기"],
    correctAnswer: "중독 인정"
  }
];

function Quiz({ onReturnHome }) {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);

  const handleAnswerClick = (selectedAnswer) => {
    if (selectedAnswer === quizData[currentQuestion].correctAnswer) {
      setScore(score + 1);
    }

    const nextQuestion = currentQuestion + 1;
    if (nextQuestion < quizData.length) {
      setCurrentQuestion(nextQuestion);
    } else {
      setShowScore(true);
    }
  };

  const restartQuiz = () => {
    setCurrentQuestion(0);
    setScore(0);
    setShowScore(false);
  };

  return (
    <div className="quiz-container">
      <button className="home-button" onClick={onReturnHome}>홈으로 돌아가기</button>
      {showScore ? (
        <div className="score-section">
          당신의 점수: {score} / {quizData.length}
          <button onClick={restartQuiz}>다시 시작</button>
        </div>
      ) : (
        <>
          <div className="question-section">
            <div className="question-count">
              <span>질문 {currentQuestion + 1}</span>/{quizData.length}
            </div>
            <div className="question-text">{quizData[currentQuestion].question}</div>
          </div>
          <div className="answer-section">
            {quizData[currentQuestion].options.map((option, index) => (
              <button key={index} onClick={() => handleAnswerClick(option)}>
                {option}
              </button>
            ))}
          </div>
        </>
      )}
    </div>
  );
}

export default Quiz;
