import React, { useState } from 'react';
import './App.css';

function App() {
  const [isChatbotOpen, setIsChatbotOpen] = useState(false);

  const toggleChatbot = () => {
    setIsChatbotOpen(!isChatbotOpen);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>마약 예방 교육 센터</h1>
      </header>
      <main className="App-main">
        <section className="info-section">
          <h2>마약의 위험성</h2>
          <p>마약은 심각한 건강 문제와 사회적 문제를 일으킬 수 있습니다. 여기에서 마약의 위험성에 대해 자세히 알아보세요.</p>
        </section>
        <section className="education-section">
          <h2>예방 교육 프로그램</h2>
          <ul>
            <li>청소년 대상 교육</li>
            <li>성인 대상 워크샵</li>
            <li>온라인 교육 과정</li>
          </ul>
        </section>
        <section className="resources-section">
          <h2>도움 받을 수 있는 곳</h2>
          <p>마약 중독으로 어려움을 겪고 있다면, 아래 연락처로 도움을 요청하세요:</p>
          <p>긴급전화: 1234-5678</p>
        </section>
      </main>
      <footer className="App-footer">
        <p>© 2023 마약 예방 교육 센터. All rights reserved.</p>
      </footer>
      <button className="chatbot-button" onClick={toggleChatbot}>
        {isChatbotOpen ? '챗봇 닫기' : '챗봇 열기'}
      </button>
      {isChatbotOpen && (
        <div className="chatbot-container">
          <h3>챗봇</h3>
          <p>여기에 챗봇 인터페이스가 들어갑니다.</p>
          {/* 실제 챗봇 구현을 여기에 추가할 수 있습니다 */}
        </div>
      )}
    </div>
  );
}

export default App;
