import React, { useState } from 'react';
import './App.css';
import Quiz from './Quiz';
import Header from './Header';
import StartScreen from './StartScreen';
import { FullPageChat } from "flowise-embed-react";

function App() {
  const [showQuiz, setShowQuiz] = useState(false);
  const [started, setStarted] = useState(false);

  const handleStart = () => {
    setStarted(true);
  };

  if (!started) {
    return <StartScreen onStart={handleStart} />;
  }

  return (
    <div className="App">
      <Header 
        showQuizButton={!showQuiz} 
        onQuizClick={() => setShowQuiz(true)} 
        onHomeClick={() => setShowQuiz(false)}
      />
      <main className="App-main">
        {showQuiz ? (
          <Quiz />
        ) : (
          <>
            <section className="chatbot-section">
              <h2>챗봇에게 물어보세요</h2>
              <div className="chat-container">
                <FullPageChat
                  chatflowid="bf97e04d-7f6e-4500-b490-e4ee644f9526"
                  apiHost="https://flow.edutrack.kr"
                />
              </div>
            </section>
            <section className="news-section">
              <h2>관련 뉴스</h2>
              <div className="article-container">
                <div className="article-scroll">
                  {newsArticles.concat(newsArticles).map((article, index) => (
                    <article key={index} className="news-article">
                      <h3>{article.title}</h3>
                      <p>{article.summary}</p>
                      <a href={article.link} target="_blank" rel="noopener noreferrer">자세히 보기</a>
                    </article>
                  ))}
                </div>
              </div>
            </section>
          </>
        )}
      </main>
      <footer className="App-footer">
        <p>© 2023 마약 예방 교육 센터. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
